import { React, Component } from 'react';
import { withRouter } from 'react-router-dom';
import './Profile.css';

class CustomerProfile extends Component {
    state = {
        email: '',
        firstName: '',
        lastName: '',
        phoneNumber: '',
        birthDate: '',
        city: '',
        address: '',
        subscription: '',
        in_edit: false
    }

    handleEditButton = (event) => {
        if (this.state.in_edit) {
            event.preventDefault();
    
            const user = {
                email: this.state.email,
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                phoneNumber: this.state.phoneNumber,
                birthDate: this.state.birthDate,
                city: this.state.city,
                address: this.state.address,
                subscription: this.state.subscription
            };

            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(user)
            };
            fetch('/Titsuite-1.0-SNAPSHOT/api/users/profile/update', requestOptions)
                .then(res => res.json())
                .then((data) => {
                    console.log(data);
                }
            );
        }
        this.setState(
            {
                in_edit: !this.state.in_edit
            }
        );
    }

    handleChange = (event) => {
        console.log(event);
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    handleLogout = (event) => {
        event.preventDefault();

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch('/Titsuite-1.0-SNAPSHOT/api/users/logout', requestOptions).then(res => res.json()).then((data) => {
            console.log(data);
            this.props.history.push('/login');
        });
    }

    componentDidMount() {
        const requestOptions = {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' }
        };
        fetch('/Titsuite-1.0-SNAPSHOT/api/users/profile', requestOptions).then(res => res.json()).then((data) => {
            console.log(data);
            if (data != null) {
                this.setState(
                    {
                        email: data.email,
                        firstName: data.firstName,
                        lastName: data.lastName,
                        phoneNumber: data.phoneNumber,
                        birthDate: data.birthDate,
                        city: data.city,
                        address: data.address,
                        subscription: data.subscription
                    }
                );
            }
        });
    }

    render() {
        return (
            <div className="container">
                <button onClick={this.handleLogout} className="customButton logoutButton">Logout</button>
                <div className="main-body">
                    <div className="col-md-8">
                        <h2>Freelancer Profile</h2>
                        <div className="card mb-3">
                            <div className="card-body">
                                <div className="row">
                                    <button onClick={this.handleEditButton} className="customButton padded-left">{this.state.in_edit ? 'Save' : 'Edit'}</button>
                                    <h3 className="padded-up">Personal Details</h3>
                                    <div className="col-sm-3">
                                        <h5 className="mb-0">Email</h5>
                                    </div>
                                    <div className="col-sm-9 text-secondary">
                                        {this.state.email}
                                    </div>                   
                                </div>
                                <hr />
                                <div className="row">
                                    <div className="col-sm-3">
                                        <h5 className="mb-0">First Name</h5>
                                    </div>
                                    <div contentEditable={this.state.in_edit} onChange={this.handleChange} className="col-sm-9 text-secondary">
                                        {this.state.firstName}
                                    </div>
                                </div>
                                <hr />
                                <div className="row">
                                    <div className="col-sm-3">
                                        <h5 className="mb-0">Last Name</h5>
                                    </div>
                                    <div contentEditable={this.state.in_edit} onChange={this.handleChange} className="col-sm-9 text-secondary">
                                        {this.state.lastName}
                                    </div>
                                </div>
                                <hr />
                                <div className="row">
                                    <div className="col-sm-3">
                                        <h5 className="mb-0">Phone Number</h5>
                                    </div>
                                    <div contentEditable={this.state.in_edit} onChange={this.handleChange} className="col-sm-9 text-secondary">
                                        {this.state.phoneNumber}
                                    </div>
                                </div>
                                <hr />
                                <div className="row">
                                    <div className="col-sm-3">
                                        <h5 className="mb-0">City</h5>
                                    </div>
                                    <div contentEditable={this.state.in_edit} onChange={this.handleChange} className="col-sm-9 text-secondary">
                                        {this.state.city}
                                    </div>
                                </div>
                                <hr />
                                <div className="row">
                                    <div className="col-sm-3">
                                        <h5 className="mb-0">Address</h5>
                                    </div>
                                    <div contentEditable={this.state.in_edit} onChange={this.handleChange} className="col-sm-9 text-secondary">
                                        {this.state.address}
                                    </div>
                                </div>
                                <hr />
                                <div className="row">
                                    <div className="col-sm-3">
                                        <h5 className="mb-0">Subscription</h5>
                                    </div>
                                    <div contentEditable={this.state.in_edit} onChange={this.handleChange} className="col-sm-9 text-secondary">
                                        {this.state.subscription}
                                    </div>
                                </div>
                                <hr />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(CustomerProfile);
